% Autor: Marcel Ochsendorf% Datum: 10.05.2017% stammbaum http://www.r-krell.de/if-prolog-stammbaum.gifweiblich(monika).maennlich(ulrich).maennlich(klaus).weiblich(petra).maennlich(frank).weiblich(claudia).maennlich(benni).weiblich(sylvia).maennlich(karl).weiblich(mia).maennlich(torsten).kind(klaus, monika).kind(klaus, ulrich).kind(sylvia, monika).kind(sylvia, ulrich).kind(frank,klaus).kind(frank,petra).kind(claudia,klaus).kind(claudia,petra).kind(benni,klaus).kind(benni,petra).kind(mia, sylvia).kind(mia,karl).kind(torsten, sylvia).kind(torsten, karl).%V ist der Vater von K :- V ist maennlich und K ist das Kind von Vvater(V,K) :- maennlich(V), kind(V,K).%M ist die Mutter von K :- M ist weiblich und K ist das Kind von Mmutter(M,K) :- weiblich(M), kind(M,K).%S ist der Sohn von E :- S ist maennlich und E ist Vater oder Mutter%sohn(S,E) :- maennlich(S), vater(E,S).%sohn(S,E) :- maennlich(S), mutter(E,S).sohn(S,E) :- eltern(S,R), maennlich(S).%T ist die Tochter von E :- T ist weiblich und E ist Vater oder Mutter nicht selbertochter(T,E) :- weiblich(T), mutter(E,T).tochter(T,E) :- weiblich(T), vater(E,T).%B ist Bruder von G :- B ist maennlich und B und G haben denselben Vater und dieselbe Mutter und B ist ungleich Gbruder(B,G) :- maennlich(B), vater(V,B), vater(V,G), mutter(M,G), mutter(M,B), B \= G.%S ist Schwester von G :- S ist weiblich und S und G haben denselben Vater und dieselbe Mutter und S ist ungleich Gschwester(S,G) :- weiblich(S), vater(V,S), vater(V,G), mutter(M,G), mutter(M,S), S \= G.% X schwester/bruder von  Ygeschwister(X,Y) :- vater(V,X), vater(V,Y), mutter(M,X), mutter(M,Y).%O ist der Onkel von X :- O ist maennlich und der Bruder von E und X ist das Kind Eonkel(O,X) :- maennlich(O), bruder(O,E), kind(E,X).%T ist die Tante von X :- T ist weiblich und die Schwester von E und X ist das Kind Etante(T,X) :- weiblich(T), schwester(T,E), kind(E,X).%C ist die couseine/cousin von X muss das kind von onkel/cousine seincousin(C,X) :- sohn(C,M), geschwister(M,Y), kind(Y,X).cousine(C,X) :- tochter(C,M), geschwister(M,Y), kind(Y,X).grossvater(G,E) :- vater(G,V), vater(V,E).grossvater(G,E) :- vater(G,V), mutter(V,E).%grossmutter(G,E) :- mutter(G,M), vater(M,E).grossmutter(G,E) :- mutter(G,M), mutter(M,E).